import axios from "axios";


axios.defaults.baseURL="http://localhost:8085/"

var headers = function() {
    return ({
        "Authorization": `${localStorage.getItem("access_token")}`
    });
};

headers()

axios.interceptors.request.use(config =>{
    config
    return headers()

}, error=>{
        return Promise.reject(error)
    })
