import axios from "axios";


axios.defaults.baseURL="http://localhost:8085/"




axios.interceptors.request.use(config =>{
    config.headers['Authorization'] = 'Bearer ' + localStorage.getItem("access_token")
    return config

}, error=>{
        return Promise.reject(error)
    })
